DROP TABLE IF EXISTS `fb_novel`;
CREATE TABLE `fb_novel` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `book_id` int(11) NOT NULL DEFAULT '0' COMMENT '作品编号',
  `name` varchar(20) NOT NULL DEFAULT '' COMMENT '名称',
  `description` text NOT NULL COMMENT '描述',
  `keyword` varchar(100) NOT NULL DEFAULT '' COMMENT '关键字',
  `image` varchar(100) NOT NULL DEFAULT '' COMMENT '封面图',
  `hit` int(11) NOT NULL DEFAULT '0' COMMENT '点击数',
  `recommend` int(11) NOT NULL DEFAULT '0' COMMENT '推荐数',
  `updateTime` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  `created` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='小说信息表';

-- ----------------------------
-- Records of fb_novel
-- ----------------------------
INSERT INTO `fb_novel` VALUES ('1', '1', 'Ｒｅ：ゼロから始める異世界生活', '突如、コンビニ帰りに異世界へ召喚されたひきこもり学生の菜月昴。知識も技術も武力もコミュ能力もない、ないない尽くしの凡人が、チートボーナスを与えられることもなく放り込まれた異世界で必死こいて生き抜く。彼に与えられたたった一個の祝福は、『死んだら巻き戻ります』という痛みを伴う『死に戻り』のみ！　頼れるもののいない異世界で、いったい彼は何度死に、なにを掴み取るのか。　　※血も死体も出る予定ですが、そんな派手なことにはなりません。\r\n\r\n※当作品は2014年1月24日、ＭＦ文庫Ｊ様の方から同タイトルで書籍化させていただいております。皆様の応援のおかげです、ありがとうございます。また、ＷＥＢ版の削除・ダイジェスト化・更新停止といったことは今後も予定されておりません。書籍・ＷＥＢ共によろしくお願いします。', '', '', '0', '0', '2016-06-02 11:24:39', '2016-06-02 11:24:39');

