-- ----------------------------
-- Table structure for fb_role
-- ----------------------------
DROP TABLE IF EXISTS `fb_role`;
CREATE TABLE `fb_role` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `book_id` int(11) NOT NULL DEFAULT '0' COMMENT '作品编号',
  `name` varchar(20) NOT NULL DEFAULT '' COMMENT '角色名字',
  `description` text NOT NULL COMMENT '角色描述',
  `image` text NOT NULL COMMENT '角色图片',
  `hit` int(11) NOT NULL DEFAULT '0' COMMENT '点击数',
  `recommend` int(11) NOT NULL DEFAULT '0' COMMENT '推荐数',
  `updateTime` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '更新时间',
  `created` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8 COMMENT='作品角色表';

-- ----------------------------
-- Records of fb_role
-- ----------------------------
INSERT INTO `fb_role` VALUES ('1', '1', 'レム - 蕾姆', '在罗斯沃尔的宅邸工作的双胞胎女仆妹妹，家务全能，拥有鬼族的能力，但由于只有半只角而不能发挥全力。在小说第三卷经历了一连串事件之后对男主抱有好感。[7]  即使知道昴的心意前提下依旧跟昴告白，对其不离不弃，并且跟昴始探讨一夫多妻的可能性。在昴被女主不理解而冷漠，因不成熟又不断地犯错而落到死亡轮回中，从而感到失落绝望的时候，她让昴重新找回了要走向完美结局的动力，在多个轮回中为男主惨死多次，最后感化男主使男主喜欢上她，虽然不知道男主可以轮回但完全信任男主。\r\n在if路线中，与昴逃亡至卡拉拉奇，并与昴有一儿一女。\r\n人物性格：乐观、有点坏心眼（时不时捉弄男主）。', 'http://image.fairybook.com/1/role/1.jpg', '0', '0', '2016-06-02 11:24:39', '2016-06-02 11:24:39');
INSERT INTO `fb_role` VALUES ('2', '1', 'エミリア - 艾米丽雅', '本作女主，年龄不详，疑似是’嫉妒魔女“的转生而令人畏惧和憎恨，精灵术师，混血的半妖精，曾经救过男主。之后男主便追随着她。[3] \r\n对男主宣称自己叫做莎缇拉（意为“嫉妒魔女”）其目的是为了不想男主陷入自己的麻烦中才那么说的。小说第一卷最后男主得知其真名为爱蜜莉雅，是卢克尼卡王国第42代的国王候补人之一。[6]  因自身是半精灵而被世人忌讳和恐惧，渴望自己能得到跟普通人一样的待遇。\r\n人物性格：傲娇、迷糊、不坦率。', ' http://image.fairybook.com/1/role/2.jpg', '0', '0', '2016-06-03 09:09:01', '2016-06-03 09:09:01');

-- ----------------------------
-- Table structure for fb_user
-- ----------------------------
DROP TABLE IF EXISTS `fb_user`;
CREATE TABLE `fb_user` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户编号',
  `accountNumber` varchar(200) NOT NULL DEFAULT '' COMMENT '用户账号',
  `password` varchar(50) NOT NULL DEFAULT '' COMMENT '密码',
  `name` varchar(20) NOT NULL DEFAULT '' COMMENT '昵称',
  `loginTime` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '登录时间',
  `created` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COMMENT='用户信息表';

